    private void CampoBarrasKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_CampoBarrasKeyTyped
        char c = evt.getKeyChar();
        if (!Character.isDigit(c)){
            evt.consume();
        }
    }//GEN-LAST:event_CampoBarrasKeyTyped

