    private void CampoQtdKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_CampoQtdKeyTyped
        char c = evt.getKeyChar();
        if (!Character.isDigit(c))
        evt.consume();
    }//GEN-LAST:event_CampoQtdKeyTyped

